import React, { useState, useRef } from 'react';
import { Copy, Check, Paperclip, X } from 'lucide-react';

// Componente para renderizar bloques de código con estilo moderno
const CodeBlock = ({ code, language }) => {
  const [copied, setCopied] = useState(false);

  const handleCopy = async () => {
    try {
      await navigator.clipboard.writeText(code);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error('Error al copiar:', err);
    }
  };

  return (
    <div style={{
      position: 'relative',
      marginBottom: '16px',
      borderRadius: '8px',
      overflow: 'hidden',
      backgroundColor: '#1e1e1e',
      border: '1px solid #3e3e3e'
    }}>
      {/* Header del bloque de código */}
      <div style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: '8px 16px',
        backgroundColor: '#2d2d2d',
        borderBottom: '1px solid #3e3e3e'
      }}>
        <span style={{
          fontSize: '12px',
          color: '#858585',
          fontFamily: 'monospace',
          textTransform: 'lowercase'
        }}>
          {language || 'code'}
        </span>
        <button
          onClick={handleCopy}
          style={{
            display: 'flex',
            alignItems: 'center',
            gap: '6px',
            padding: '4px 12px',
            backgroundColor: copied ? '#4CAF50' : 'transparent',
            color: copied ? '#fff' : '#858585',
            border: '1px solid',
            borderColor: copied ? '#4CAF50' : '#3e3e3e',
            borderRadius: '4px',
            cursor: 'pointer',
            fontSize: '12px',
            transition: 'all 0.2s ease',
            fontFamily: 'system-ui, -apple-system, sans-serif'
          }}
          onMouseEnter={(e) => {
            if (!copied) {
              e.currentTarget.style.backgroundColor = '#3e3e3e';
              e.currentTarget.style.color = '#fff';
            }
          }}
          onMouseLeave={(e) => {
            if (!copied) {
              e.currentTarget.style.backgroundColor = 'transparent';
              e.currentTarget.style.color = '#858585';
            }
          }}
        >
          {copied ? (
            <>
              <Check size={14} />
              Copiado
            </>
          ) : (
            <>
              <Copy size={14} />
              Copiar
            </>
          )}
        </button>
      </div>

      {/* Código */}
      <pre style={{
        margin: 0,
        padding: '16px',
        overflow: 'auto',
        fontSize: '14px',
        lineHeight: '1.5',
        fontFamily: 'Consolas, Monaco, "Courier New", monospace',
        color: '#d4d4d4',
        backgroundColor: '#1e1e1e'
      }}>
        <code>{code}</code>
      </pre>
    </div>
  );
};

// Componente para renderizar la respuesta con bloques de código
const ResponseDisplay = ({ response, model }) => {
  const parseResponse = (text) => {
    const parts = [];
    const codeBlockRegex = /```(\w+)?\n([\s\S]*?)```/g;
    let lastIndex = 0;
    let match;

    while ((match = codeBlockRegex.exec(text)) !== null) {
      if (match.index > lastIndex) {
        const textContent = text.slice(lastIndex, match.index);
        if (textContent.trim()) {
          parts.push({
            type: 'text',
            content: textContent
          });
        }
      }

      parts.push({
        type: 'code',
        language: match[1] || 'code',
        content: match[2].trim()
      });

      lastIndex = match.index + match[0].length;
    }

    if (lastIndex < text.length) {
      const textContent = text.slice(lastIndex);
      if (textContent.trim()) {
        parts.push({
          type: 'text',
          content: textContent
        });
      }
    }

    return parts.length > 0 ? parts : [{ type: 'text', content: text }];
  };

  const parts = parseResponse(response);

  return (
    <div style={{
      backgroundColor: '#e8f5e8',
      border: '1px solid #4CAF50',
      borderRadius: '8px',
      padding: '20px',
      marginBottom: '20px'
    }}>
      <h3 style={{
        color: '#2e7d32',
        margin: '0 0 15px 0',
        fontSize: '1.2em'
      }}>
        ✅ Respuesta del modelo ({model}):
      </h3>

      <div>
        {parts.map((part, index) => {
          if (part.type === 'code') {
            return (
              <CodeBlock
                key={index}
                code={part.content}
                language={part.language}
              />
            );
          } else {
            return (
              <pre key={index} style={{
                backgroundColor: '#f5f5f5',
                border: '1px solid #ddd',
                borderRadius: '6px',
                padding: '15px',
                margin: '0 0 16px 0',
                overflow: 'auto',
                fontSize: '14px',
                lineHeight: '1.5',
                fontFamily: 'system-ui, -apple-system, sans-serif',
                whiteSpace: 'pre-wrap',
                wordBreak: 'break-word'
              }}>
                {part.content}
              </pre>
            );
          }
        })}
      </div>
    </div>
  );
};

function App() {
  const [prompt, setPrompt] = useState('');
  const [response, setResponse] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [model, setModel] = useState('deepseek-coder:6.7b');
  const [temperature, setTemperature] = useState(0.7);
  const [maxTokens, setMaxTokens] = useState(2048);
  const [showAdvanced, setShowAdvanced] = useState(false);
  const [hasSubmitted, setHasSubmitted] = useState(false);
  const [attachedFile, setAttachedFile] = useState(null);
  const [fileContent, setFileContent] = useState('');
  const fileInputRef = useRef(null);

  const commonModels = [
    { name: 'phi:2.7b', memory: '~1.6GB', description: '⭐ Ligero - Recomendado para 2GB RAM' },
    { name: 'tinyllama:1.1b', memory: '~700MB', description: '🚀 Ultra ligero - Básico pero rápido' },
    { name: 'qwen:1.8b', memory: '~1.1GB', description: '💡 Pequeño pero potente' },
    { name: 'deepseek-coder:1.3b', memory: '~800MB', description: '👨‍💻 Código - Ultra ligero' },
    { name: 'deepseek-coder:6.7b', memory: '~3.3GB', description: '❌ Requiere 4GB+ RAM' },
    { name: 'llama2:7b', memory: '~3.8GB', description: '❌ Requiere 4GB+ RAM' },
    { name: 'codellama:7b', memory: '~3.8GB', description: '❌ Requiere 4GB+ RAM' }
  ];

  const predefinedPrompts = [
    "Escribe un hola mundo en Python",
    "Crea una función en JavaScript que ordene un array",
    "Explica qué es recursión con un ejemplo",
    "Crea un componente React simple",
    "Escribe una consulta SQL básica"
  ];

  const handleFileSelect = (e) => {
    const file = e.target.files[0];
    if (file) {
      setAttachedFile(file);
      const reader = new FileReader();
      reader.onload = (event) => {
        setFileContent(event.target.result);
      };
      reader.readAsText(file);
    }
  };

  const handleRemoveFile = () => {
    setAttachedFile(null);
    setFileContent('');
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!prompt.trim()) return;

    setIsLoading(true);
    setError('');
    setResponse('');
    setHasSubmitted(true);

    try {
      let finalPrompt = prompt;

      // Si hay un archivo adjunto, agregar su contenido al prompt
      if (fileContent) {
        finalPrompt = `Contexto del archivo "${attachedFile.name}":\n\n${fileContent}\n\n---\n\nConsulta del usuario: ${prompt}`;
      }

      const result = await fetch('http://localhost:3001/api/generate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          model: model,
          prompt: finalPrompt,
          options: {
            temperature: 0.7,
            num_predict: 512,
            top_k: 40,
            top_p: 0.9,
            repeat_penalty: 1.1
          }
        })
      });

      if (!result.ok) {
        throw new Error(`HTTP error! status: ${result.status}`);
      }

      const data = await result.json();
      setResponse(data.response);
    } catch (err) {
      setError(`Error: ${err.message}`);
      if (err.message.includes('fetch') || err.message.includes('Failed to fetch')) {
        setError('No se puede conectar al servidor backend. Asegúrate de que esté ejecutándose en localhost:3001');
      }
    } finally {
      setIsLoading(false);
    }
  };

  const clearAll = () => {
    setPrompt('');
    setResponse('');
    setError('');
    setHasSubmitted(false);
    handleRemoveFile();
  };

  return (
    <div style={{
      fontFamily: 'Arial, sans-serif',
      maxWidth: '1000px',
      margin: '0 auto',
      padding: '20px',
      backgroundColor: '#f5f5f5',
      minHeight: '100vh'
    }}>
      <div style={{
        backgroundColor: 'white',
        borderRadius: '12px',
        padding: '30px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
      }}>
        {/* Header */}
        <div style={{
          textAlign: 'center',
          marginBottom: '30px',
          borderBottom: '2px solid #e0e0e0',
          paddingBottom: '20px'
        }}>
          <h1 style={{
            color: '#333',
            margin: '0 0 10px 0',
            fontSize: '2.2em'
          }}>
            🤖 Interfaz Ollama
          </h1>
          <p style={{
            color: '#666',
            margin: '0',
            fontSize: '1.1em'
          }}>
            Backend: localhost:3001 → Ollama: localhost:11434
          </p>
        </div>

        {/* Model Selection and Advanced Options */}
        <div style={{ marginBottom: '20px' }}>
          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
            gap: '15px',
            marginBottom: '15px'
          }}>
            <div>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                fontWeight: 'bold',
                color: '#333'
              }}>
                Modelo:
              </label>
              <select
                value={model}
                onChange={(e) => setModel(e.target.value)}
                disabled={isLoading}
                style={{
                  width: '100%',
                  padding: '10px',
                  border: '2px solid #ddd',
                  borderRadius: '6px',
                  fontSize: '14px',
                  backgroundColor: isLoading ? '#f9f9f9' : 'white'
                }}
              >
                {commonModels.map(m => (
                  <option key={m.name} value={m.name}>
                    {m.name} - {m.memory} - {m.description}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                fontWeight: 'bold',
                color: '#333'
              }}>
                Opciones avanzadas:
              </label>
              <button
                type="button"
                onClick={() => setShowAdvanced(!showAdvanced)}
                style={{
                  padding: '10px 15px',
                  backgroundColor: showAdvanced ? '#4CAF50' : '#2196F3',
                  color: 'white',
                  border: 'none',
                  borderRadius: '6px',
                  fontSize: '14px',
                  cursor: 'pointer',
                  width: '100%'
                }}
              >
                {showAdvanced ? '🔧 Ocultar opciones' : '⚙️ Mostrar opciones'}
              </button>
            </div>
          </div>

          {/* Advanced Options */}
          {showAdvanced && (
            <div style={{
              backgroundColor: '#f8f9fa',
              padding: '20px',
              borderRadius: '8px',
              border: '1px solid #dee2e6',
              marginBottom: '15px'
            }}>
              <div style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
                gap: '15px'
              }}>
                <div>
                  <label style={{
                    display: 'block',
                    marginBottom: '8px',
                    fontWeight: 'bold',
                    color: '#333',
                    fontSize: '14px'
                  }}>
                    Temperatura: {temperature}
                  </label>
                  <input
                    type="range"
                    min="0"
                    max="2"
                    step="0.1"
                    value={temperature}
                    onChange={(e) => setTemperature(parseFloat(e.target.value))}
                    disabled={isLoading}
                    style={{ width: '100%' }}
                  />
                  <small style={{ color: '#666' }}>
                    Creatividad (0 = conservador, 2 = muy creativo)
                  </small>
                </div>

                <div>
                  <label style={{
                    display: 'block',
                    marginBottom: '8px',
                    fontWeight: 'bold',
                    color: '#333',
                    fontSize: '14px'
                  }}>
                    Max Tokens: {maxTokens}
                  </label>
                  <input
                    type="range"
                    min="256"
                    max="4096"
                    step="256"
                    value={maxTokens}
                    onChange={(e) => setMaxTokens(parseInt(e.target.value))}
                    disabled={isLoading}
                    style={{ width: '100%' }}
                  />
                  <small style={{ color: '#666' }}>
                    Longitud máxima de respuesta
                  </small>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Error Display */}
        {error && (
          <div style={{
            backgroundColor: '#ffebee',
            border: '1px solid #f44336',
            borderRadius: '8px',
            padding: '20px',
            marginBottom: '20px'
          }}>
            <h3 style={{
              color: '#f44336',
              margin: '0 0 10px 0',
              fontSize: '1.2em'
            }}>
              ❌ Error
            </h3>
            <p style={{
              color: '#d32f2f',
              margin: '0',
              fontSize: '16px'
            }}>
              {error}
            </p>
          </div>
        )}

        {/* Response Display */}
        {response && (
          <ResponseDisplay
            response={response}
            model={model}
          />
        )}

        {/* Loading Display */}
        {isLoading && (
          <div style={{
            textAlign: 'center',
            padding: '30px',
            backgroundColor: '#f0f8ff',
            border: '1px solid #2196F3',
            borderRadius: '8px',
            marginBottom: '20px'
          }}>
            <div style={{
              display: 'inline-block',
              width: '40px',
              height: '40px',
              border: '4px solid #f3f3f3',
              borderTop: '4px solid #2196F3',
              borderRadius: '50%',
              animation: 'spin 1s linear infinite',
              marginBottom: '15px'
            }}></div>
            <p style={{
              color: '#1976D2',
              margin: '0',
              fontSize: '16px',
              fontWeight: 'bold'
            }}>
              Procesando con {model}... ⚡
            </p>
          </div>
        )}

        {/* Input Section - Movido hacia abajo */}
        <div style={{
          marginTop: '20px',
          borderTop: hasSubmitted ? '1px solid #e0e0e0' : 'none',
          paddingTop: hasSubmitted ? '20px' : '0'
        }}>
          {/* Archivo adjunto preview */}
          {attachedFile && (
            <div style={{
              backgroundColor: '#e3f2fd',
              border: '1px solid #2196F3',
              borderRadius: '8px',
              padding: '12px',
              marginBottom: '15px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'space-between'
            }}>
              <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                <Paperclip size={18} color="#1976d2" />
                <div>
                  <div style={{ fontWeight: 'bold', color: '#1976d2', fontSize: '14px' }}>
                    {attachedFile.name}
                  </div>
                  <div style={{ fontSize: '12px', color: '#666' }}>
                    {(attachedFile.size / 1024).toFixed(2)} KB
                  </div>
                </div>
              </div>
              <button
                onClick={handleRemoveFile}
                style={{
                  background: 'transparent',
                  border: 'none',
                  cursor: 'pointer',
                  color: '#f44336',
                  padding: '5px',
                  display: 'flex',
                  alignItems: 'center'
                }}
              >
                <X size={20} />
              </button>
            </div>
          )}

          <div style={{ position: 'relative' }}>
            <textarea
              value={prompt}
              onChange={(e) => setPrompt(e.target.value)}
              placeholder="Escribe tu consulta aquí..."
              disabled={isLoading}
              style={{
                width: '100%',
                minHeight: '120px',
                padding: '15px 50px 15px 15px',
                border: '2px solid #ddd',
                borderRadius: '8px',
                fontSize: '16px',
                fontFamily: 'inherit',
                resize: 'vertical',
                outline: 'none',
                transition: 'border-color 0.3s',
                backgroundColor: isLoading ? '#f9f9f9' : 'white'
              }}
              onFocus={(e) => e.target.style.borderColor = '#4CAF50'}
              onBlur={(e) => e.target.style.borderColor = '#ddd'}
            />

            {/* Botón de adjuntar archivo */}
            <button
              onClick={() => fileInputRef.current?.click()}
              disabled={isLoading}
              style={{
                position: 'absolute',
                right: '15px',
                top: '15px',
                background: 'transparent',
                border: 'none',
                cursor: isLoading ? 'not-allowed' : 'pointer',
                padding: '8px',
                borderRadius: '6px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                transition: 'background-color 0.2s'
              }}
              onMouseEnter={(e) => {
                if (!isLoading) e.currentTarget.style.backgroundColor = '#f0f0f0';
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.backgroundColor = 'transparent';
              }}
            >
              <Paperclip size={20} color={isLoading ? '#ccc' : '#666'} />
            </button>

            <input
              ref={fileInputRef}
              type="file"
              accept=".sql,.txt,.js,.py,.java,.cpp,.c,.html,.css,.json,.xml,.csv"
              onChange={handleFileSelect}
              style={{ display: 'none' }}
            />
          </div>

          {/* Buttons */}
          <div style={{
            display: 'flex',
            gap: '15px',
            marginTop: '15px',
            flexWrap: 'wrap'
          }}>
            <button
              onClick={handleSubmit}
              disabled={isLoading || !prompt.trim()}
              style={{
                padding: '12px 24px',
                backgroundColor: isLoading || !prompt.trim() ? '#ccc' : '#4CAF50',
                color: 'white',
                border: 'none',
                borderRadius: '8px',
                fontSize: '16px',
                fontWeight: 'bold',
                cursor: isLoading || !prompt.trim() ? 'not-allowed' : 'pointer',
                transition: 'background-color 0.3s',
                minWidth: '130px'
              }}
            >
              {isLoading ? '🔄 Generando...' : '🚀 Enviar'}
            </button>
            <button
              onClick={clearAll}
              disabled={isLoading}
              style={{
                padding: '12px 24px',
                backgroundColor: isLoading ? '#ccc' : '#f44336',
                color: 'white',
                border: 'none',
                borderRadius: '8px',
                fontSize: '16px',
                fontWeight: 'bold',
                cursor: isLoading ? 'not-allowed' : 'pointer',
                transition: 'background-color 0.3s',
                minWidth: '130px'
              }}
            >
              🗑️ Limpiar
            </button>
          </div>

          {/* Predefined Prompts - Solo se muestran si no ha enviado consultas */}
          {!hasSubmitted && (
            <div style={{ marginTop: '20px' }}>
              <label style={{
                display: 'block',
                marginBottom: '10px',
                fontWeight: 'bold',
                color: '#666',
                fontSize: '0.9em'
              }}>
                💡 Consultas de ejemplo:
              </label>
              <div style={{
                display: 'flex',
                flexWrap: 'wrap',
                gap: '8px'
              }}>
                {predefinedPrompts.map((predPrompt, index) => (
                  <button
                    key={index}
                    onClick={() => setPrompt(predPrompt)}
                    disabled={isLoading}
                    style={{
                      padding: '8px 12px',
                      backgroundColor: '#e3f2fd',
                      color: '#1976d2',
                      border: '1px solid #bbdefb',
                      borderRadius: '20px',
                      fontSize: '12px',
                      cursor: isLoading ? 'not-allowed' : 'pointer',
                      transition: 'all 0.2s'
                    }}
                    onMouseOver={(e) => {
                      if (!isLoading) {
                        e.target.style.backgroundColor = '#bbdefb';
                      }
                    }}
                    onMouseOut={(e) => {
                      if (!isLoading) {
                        e.target.style.backgroundColor = '#e3f2fd';
                      }
                    }}
                  >
                    {predPrompt}
                  </button>
                ))}
              </div>
            </div>
          )}
        </div>
      </div>

      <style>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }

        * {
          box-sizing: border-box;
        }

        body {
          margin: 0;
          padding: 0;
          background-color: #f5f5f5;
        }
      `}</style>
    </div>
  );
}

export default App;
