import React, { useState } from 'react';

function App() {
  const [prompt, setPrompt] = useState('');
  const [response, setResponse] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!prompt.trim()) return;

    setIsLoading(true);
    setError('');
    setResponse('');

    try {
      const result = await fetch('http://localhost:3001/api/generate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          model: 'deepseek-coder:6.7b',
          prompt: prompt,
          options: {
            num_predict: 512  // Reducido para evitar problemas de memoria
          }
        })
      });

      if (!result.ok) {
        throw new Error(`HTTP error! status: ${result.status}`);
      }

      const data = await result.json();
      setResponse(data.response);
    } catch (err) {
      console.error('Frontend error:', err);

      if (err.message.includes('fetch') || err.message.includes('Failed to fetch')) {
        setError('üîå No se puede conectar al servidor backend (localhost:3001). Verifica que est√© ejecut√°ndose.');
      } else if (result?.status === 500) {
        setError(' Error interno del servidor. Revisa los logs del backend y que Ollama est√© funcionando.');
      } else if (result?.status === 503) {
        setError('üîß El backend no puede conectar con Ollama (localhost:11434). Verifica que Ollama est√© ejecut√°ndose.');
      } else {
        setError(`‚ùå Error: ${err.message}`);
      }
    } finally {
      setIsLoading(false);
    }
  };

  const clearAll = () => {
    setPrompt('');
    setResponse('');
    setError('');
  };

  return (
    <div style={{
      fontFamily: 'Arial, sans-serif',
      maxWidth: '800px',
      margin: '0 auto',
      padding: '20px',
      backgroundColor: '#f5f5f5',
      minHeight: '100vh'
    }}>
      <div style={{
        backgroundColor: 'white',
        borderRadius: '12px',
        padding: '30px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
      }}>
        {/* Header */}
        <div style={{
          textAlign: 'center',
          marginBottom: '30px',
          borderBottom: '2px solid #e0e0e0',
          paddingBottom: '20px'
        }}>
          <h1 style={{
            color: '#333',
            margin: '0 0 10px 0',
            fontSize: '2.2em'
          }}>
            ü§ñ Interfaz Ollama - DeepSeek Coder (via NestJS)
          </h1>
          <p style={{
            color: '#666',
            margin: '0',
            fontSize: '1.1em'
          }}>
            Backend: localhost:3001 ‚Üí Ollama: localhost:11434
          </p>
        </div>

        {/* Input Section */}
        <div style={{ marginBottom: '20px' }}>
          <label style={{
            display: 'block',
            marginBottom: '10px',
            fontWeight: 'bold',
            color: '#333',
            fontSize: '1.1em'
          }}>
            Escribe tu consulta:
          </label>
          <textarea
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            placeholder="Ejemplo: Escribe un hola mundo en Python"
            disabled={isLoading}
            style={{
              width: '100%',
              minHeight: '120px',
              padding: '15px',
              border: '2px solid #ddd',
              borderRadius: '8px',
              fontSize: '16px',
              fontFamily: 'inherit',
              resize: 'vertical',
              outline: 'none',
              transition: 'border-color 0.3s',
              backgroundColor: isLoading ? '#f9f9f9' : 'white'
            }}
            onFocus={(e) => e.target.style.borderColor = '#4CAF50'}
            onBlur={(e) => e.target.style.borderColor = '#ddd'}
          />
        </div>

        {/* Buttons */}
        <div style={{
          display: 'flex',
          gap: '15px',
          marginBottom: '30px',
          flexWrap: 'wrap'
        }}>
          <button
            onClick={handleSubmit}
            disabled={isLoading || !prompt.trim()}
            style={{
              padding: '12px 24px',
              backgroundColor: isLoading || !prompt.trim() ? '#ccc' : '#4CAF50',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              fontSize: '16px',
              fontWeight: 'bold',
              cursor: isLoading || !prompt.trim() ? 'not-allowed' : 'pointer',
              transition: 'background-color 0.3s',
              minWidth: '130px'
            }}
          >
            {isLoading ? 'üîÑ Generando...' : 'üöÄ Enviar'}
          </button>
          <button
            onClick={clearAll}
            disabled={isLoading}
            style={{
              padding: '12px 24px',
              backgroundColor: isLoading ? '#ccc' : '#f44336',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              fontSize: '16px',
              fontWeight: 'bold',
              cursor: isLoading ? 'not-allowed' : 'pointer',
              transition: 'background-color 0.3s',
              minWidth: '130px'
            }}
          >
            üóëÔ∏è Limpiar
          </button>
        </div>

        {/* Error Display */}
        {error && (
          <div style={{
            backgroundColor: '#ffebee',
            border: '1px solid #f44336',
            borderRadius: '8px',
            padding: '20px',
            marginBottom: '20px'
          }}>
            <h3 style={{
              color: '#f44336',
              margin: '0 0 10px 0',
              fontSize: '1.2em'
            }}>
              ‚ùå Error
            </h3>
            <p style={{
              color: '#d32f2f',
              margin: '0',
              fontSize: '16px'
            }}>
              {error}
            </p>
          </div>
        )}

        {/* Response Display */}
        {response && (
          <div style={{
            backgroundColor: '#e8f5e8',
            border: '1px solid #4CAF50',
            borderRadius: '8px',
            padding: '20px',
            marginBottom: '20px'
          }}>
            <h3 style={{
              color: '#2e7d32',
              margin: '0 0 15px 0',
              fontSize: '1.2em'
            }}>
              ‚úÖ Respuesta del modelo:
            </h3>
            <pre style={{
              backgroundColor: '#f5f5f5',
              border: '1px solid #ddd',
              borderRadius: '6px',
              padding: '15px',
              margin: '0',
              overflow: 'auto',
              fontSize: '14px',
              lineHeight: '1.5',
              fontFamily: 'Consolas, Monaco, "Courier New", monospace',
              whiteSpace: 'pre-wrap',
              wordBreak: 'break-word'
            }}>
              {response}
            </pre>
          </div>
        )}

        {/* Loading Display */}
        {isLoading && (
          <div style={{
            textAlign: 'center',
            padding: '30px',
            backgroundColor: '#f0f8ff',
            border: '1px solid #2196F3',
            borderRadius: '8px'
          }}>
            <div style={{
              display: 'inline-block',
              width: '40px',
              height: '40px',
              border: '4px solid #f3f3f3',
              borderTop: '4px solid #2196F3',
              borderRadius: '50%',
              animation: 'spin 1s linear infinite',
              marginBottom: '15px'
            }}></div>
            <p style={{
              color: '#1976D2',
              margin: '0',
              fontSize: '16px',
              fontWeight: 'bold'
            }}>
              El modelo est√° procesando tu consulta...
            </p>
          </div>
        )}
      </div>

      <style>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }

        * {
          box-sizing: border-box;
        }

        body {
          margin: 0;
          padding: 0;
          background-color: #f5f5f5;
        }
      `}</style>
    </div>
  );
}
