import React, { useState } from 'react';

function App() {
  const [prompt, setPrompt] = useState('');
  const [response, setResponse] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [model, setModel] = useState('deepseek-coder:6.7b');
  const [temperature, setTemperature] = useState(0.7);
  const [maxTokens, setMaxTokens] = useState(2048);
  const [showAdvanced, setShowAdvanced] = useState(false);

  const commonModels = [
    'deepseek-coder:6.7b',
    'llama2:7b',
    'codellama:7b',
    'mistral:7b',
    'phi:2.7b'
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!prompt.trim()) return;

    setIsLoading(true);
    setError('');
    setResponse('');

    try {
      const result = await fetch('http://localhost:3001/api/generate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          model: model,
          prompt: prompt,
          options: {
            temperature: temperature,
            num_predict: maxTokens,
            top_k: 40,
            top_p: 0.9,
            repeat_penalty: 1.1
          }
        })
      });

      if (!result.ok) {
        throw new Error(`HTTP error! status: ${result.status}`);
      }

      const data = await result.json();
      setResponse(data.response);
    } catch (err) {
      setError(`Error: ${err.message}`);
      if (err.message.includes('fetch') || err.message.includes('Failed to fetch')) {
        setError('No se puede conectar al servidor backend. Asegúrate de que esté ejecutándose en localhost:3001');
      }
    } finally {
      setIsLoading(false);
    }
  };

  const clearAll = () => {
    setPrompt('');
    setResponse('');
    setError('');
  };

  const predefinedPrompts = [
    "Escribe un hola mundo en Python",
    "Crea una función en JavaScript que ordene un array",
    "Explica qué es recursión con un ejemplo",
    "Crea un componente React simple",
    "Escribe una consulta SQL básica"
  ];

  return (
    <div style={{
      fontFamily: 'Arial, sans-serif',
      maxWidth: '1000px',
      margin: '0 auto',
      padding: '20px',
      backgroundColor: '#f5f5f5',
      minHeight: '100vh'
    }}>
      <div style={{
        backgroundColor: 'white',
        borderRadius: '12px',
        padding: '30px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
      }}>
        {/* Header */}
        <div style={{
          textAlign: 'center',
          marginBottom: '30px',
          borderBottom: '2px solid #e0e0e0',
          paddingBottom: '20px'
        }}>
          <h1 style={{
            color: '#333',
            margin: '0 0 10px 0',
            fontSize: '2.2em'
          }}>
            🤖 Interfaz Ollama Avanzada
          </h1>
          <p style={{
            color: '#666',
            margin: '0',
            fontSize: '1.1em'
          }}>
            Backend: localhost:3001 → Ollama: localhost:11434
          </p>
        </div>

        {/* Model Selection and Advanced Options */}
        <div style={{ marginBottom: '20px' }}>
          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
            gap: '15px',
            marginBottom: '15px'
          }}>
            <div>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                fontWeight: 'bold',
                color: '#333'
              }}>
                Modelo:
              </label>
              <select
                value={model}
                onChange={(e) => setModel(e.target.value)}
                disabled={isLoading}
                style={{
                  width: '100%',
                  padding: '10px',
                  border: '2px solid #ddd',
                  borderRadius: '6px',
                  fontSize: '14px',
                  backgroundColor: isLoading ? '#f9f9f9' : 'white'
                }}
              >
                {commonModels.map(m => (
                  <option key={m} value={m}>{m}</option>
                ))}
              </select>
            </div>

            <div>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                fontWeight: 'bold',
                color: '#333'
              }}>
                Opciones avanzadas:
              </label>
              <button
                type="button"
                onClick={() => setShowAdvanced(!showAdvanced)}
                style={{
                  padding: '10px 15px',
                  backgroundColor: showAdvanced ? '#4CAF50' : '#2196F3',
                  color: 'white',
                  border: 'none',
                  borderRadius: '6px',
                  fontSize: '14px',
                  cursor: 'pointer',
                  width: '100%'
                }}
              >
                {showAdvanced ? '🔧 Ocultar opciones' : '⚙️ Mostrar opciones'}
              </button>
            </div>
          </div>

          {/* Advanced Options */}
          {showAdvanced && (
            <div style={{
              backgroundColor: '#f8f9fa',
              padding: '20px',
              borderRadius: '8px',
              border: '1px solid #dee2e6',
              marginBottom: '15px'
            }}>
              <div style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
                gap: '15px'
              }}>
                <div>
                  <label style={{
                    display: 'block',
                    marginBottom: '8px',
                    fontWeight: 'bold',
                    color: '#333',
                    fontSize: '14px'
                  }}>
                    Temperatura: {temperature}
                  </label>
                  <input
                    type="range"
                    min="0"
                    max="2"
                    step="0.1"
                    value={temperature}
                    onChange={(e) => setTemperature(parseFloat(e.target.value))}
                    disabled={isLoading}
                    style={{ width: '100%' }}
                  />
                  <small style={{ color: '#666' }}>
                    Creatividad (0 = conservador, 2 = muy creativo)
                  </small>
                </div>

                <div>
                  <label style={{
                    display: 'block',
                    marginBottom: '8px',
                    fontWeight: 'bold',
                    color: '#333',
                    fontSize: '14px'
                  }}>
                    Max Tokens: {maxTokens}
                  </label>
                  <input
                    type="range"
                    min="256"
                    max="4096"
                    step="256"
                    value={maxTokens}
                    onChange={(e) => setMaxTokens(parseInt(e.target.value))}
                    disabled={isLoading}
                    style={{ width: '100%' }}
                  />
                  <small style={{ color: '#666' }}>
                    Longitud máxima de respuesta
                  </small>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Predefined Prompts */}
        <div style={{ marginBottom: '20px' }}>
          <label style={{
            display: 'block',
            marginBottom: '10px',
            fontWeight: 'bold',
            color: '#333'
          }}>
            📝 Consultas de ejemplo:
          </label>
          <div style={{
            display: 'flex',
            flexWrap: 'wrap',
            gap: '8px',
            marginBottom: '15px'
          }}>
            {predefinedPrompts.map((predPrompt, index) => (
              <button
                key={index}
                onClick={() => setPrompt(predPrompt)}
                disabled={isLoading}
                style={{
                  padding: '8px 12px',
                  backgroundColor: '#e3f2fd',
                  color: '#1976d2',
                  border: '1px solid #bbdefb',
                  borderRadius: '20px',
                  fontSize: '12px',
                  cursor: isLoading ? 'not-allowed' : 'pointer',
                  transition: 'all 0.2s'
                }}
                onMouseOver={(e) => {
                  if (!isLoading) {
                    e.target.style.backgroundColor = '#bbdefb';
                  }
                }}
                onMouseOut={(e) => {
                  if (!isLoading) {
                    e.target.style.backgroundColor = '#e3f2fd';
                  }
                }}
              >
                {predPrompt}
              </button>
            ))}
          </div>
        </div>

        {/* Input Section */}
        <div style={{ marginBottom: '20px' }}>
          <label style={{
            display: 'block',
            marginBottom: '10px',
            fontWeight: 'bold',
            color: '#333',
            fontSize: '1.1em'
          }}>
            💬 Tu consulta:
          </label>
          <textarea
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            placeholder="Escribe tu consulta aquí o selecciona un ejemplo de arriba..."
            disabled={isLoading}
            style={{
              width: '100%',
              minHeight: '120px',
              padding: '15px',
              border: '2px solid #ddd',
              borderRadius: '8px',
              fontSize: '16px',
              fontFamily: 'inherit',
              resize: 'vertical',
              outline: 'none',
              transition: 'border-color 0.3s',
              backgroundColor: isLoading ? '#f9f9f9' : 'white'
            }}
            onFocus={(e) => e.target.style.borderColor = '#4CAF50'}
            onBlur={(e) => e.target.style.borderColor = '#ddd'}
          />
        </div>

        {/* Buttons */}
        <div style={{
          display: 'flex',
          gap: '15px',
          marginBottom: '30px',
          flexWrap: 'wrap'
        }}>
          <button
            onClick={handleSubmit}
            disabled={isLoading || !prompt.trim()}
            style={{
              padding: '12px 24px',
              backgroundColor: isLoading || !prompt.trim() ? '#ccc' : '#4CAF50',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              fontSize: '16px',
              fontWeight: 'bold',
              cursor: isLoading || !prompt.trim() ? 'not-allowed' : 'pointer',
              transition: 'background-color 0.3s',
              minWidth: '130px'
            }}
          >
            {isLoading ? '🔄 Generando...' : '🚀 Enviar'}
          </button>
          <button
            onClick={clearAll}
            disabled={isLoading}
            style={{
              padding: '12px 24px',
              backgroundColor: isLoading ? '#ccc' : '#f44336',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              fontSize: '16px',
              fontWeight: 'bold',
              cursor: isLoading ? 'not-allowed' : 'pointer',
              transition: 'background-color 0.3s',
              minWidth: '130px'
            }}
          >
            🗑️ Limpiar
          </button>
        </div>

        {/* Error Display */}
        {error && (
          <div style={{
            backgroundColor: '#ffebee',
            border: '1px solid #f44336',
            borderRadius: '8px',
            padding: '20px',
            marginBottom: '20px'
          }}>
            <h3 style={{
              color: '#f44336',
              margin: '0 0 10px 0',
              fontSize: '1.2em'
            }}>
              ❌ Error
            </h3>
            <p style={{
              color: '#d32f2f',
              margin: '0',
              fontSize: '16px'
            }}>
              {error}
            </p>
          </div>
        )}

        {/* Response Display */}
        {response && (
          <div style={{
            backgroundColor: '#e8f5e8',
            border: '1px solid #4CAF50',
            borderRadius: '8px',
            padding: '20px',
            marginBottom: '20px'
          }}>
            <h3 style={{
              color: '#2e7d32',
              margin: '0 0 15px 0',
              fontSize: '1.2em'
            }}>
              ✅ Respuesta del modelo ({model}):
            </h3>
            <pre style={{
              backgroundColor: '#f5f5f5',
              border: '1px solid #ddd',
              borderRadius: '6px',
              padding: '15px',
              margin: '0',
              overflow: 'auto',
              fontSize: '14px',
              lineHeight: '1.5',
              fontFamily: 'Consolas, Monaco, "Courier New", monospace',
              whiteSpace: 'pre-wrap',
              wordBreak: 'break-word'
            }}>
              {response}
            </pre>
          </div>
        )}

        {/* Loading Display */}
        {isLoading && (
          <div style={{
            textAlign: 'center',
            padding: '30px',
            backgroundColor: '#f0f8ff',
            border: '1px solid #2196F3',
            borderRadius: '8px'
          }}>
            <div style={{
              display: 'inline-block',
              width: '40px',
              height: '40px',
              border: '4px solid #f3f3f3',
              borderTop: '4px solid #2196F3',
              borderRadius: '50%',
              animation: 'spin 1s linear infinite',
              marginBottom: '15px'
            }}></div>
            <p style={{
              color: '#1976D2',
              margin: '0',
              fontSize: '16px',
              fontWeight: 'bold'
            }}>
              Procesando con {model}... ⚡
            </p>
          </div>
        )}
      </div>

      <style>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }

        * {
          box-sizing: border-box;
        }

        body {
          margin: 0;
          padding: 0;
          background-color: #f5f5f5;
        }
      `}</style>
    </div>
  );
}

export default App;
